import React, { useEffect, useState } from 'react';
import { useAuthState } from 'react-firebase-hooks/auth';
import { Link, useNavigate } from 'react-router-dom';
import {
  auth,
  registerWithEmailAndPassword,
  signInWithGoogle,
} from '../firebase_setup/firebase';

export default function Register() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [user, loading, error] = useAuthState(auth);
  const navigate = useNavigate();
  const register = () => {
    if (!name) alert("Please enter name");
    registerWithEmailAndPassword(name, email, password);
  };

  useEffect(() => {
    if (loading) return;
    if (user) navigate("/userdashboard");
  }, [user, loading]);
  return (
    <div className="register">
      <div className="register-container">
        <div className="login-form">
        <h3 className="login-title">REGISTER</h3>
        <label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Full Name"
        />
        </label>
        <label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          placeholder="Email Address"
        />
        </label>
        <label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          placeholder="Password"
        />
        </label>
        <p>Already have an account? <Link to="/login" className="underline hover:text-stone-400">Sign in</Link> now</p>
        <button className="login-buttons" onClick={register}>
          REGISTER
        </button>
        <button
          className="login-buttons"
          onClick={signInWithGoogle}
        >
          REGISTER WITH GOOGLE
        </button>
        </div>
      </div>
    </div>
  );
}
