import React, { useEffect, useState } from 'react';
import hallway from '../Images/Hallway_Background.jpg'
import {Link, useNavigate } from 'react-router-dom';
import { auth, logInWithEmailAndPassword, signInWithGoogle } from "../firebase_setup/firebase";
import { useAuthState } from 'react-firebase-hooks/auth';

export default function LogIn() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, loading, error] = useAuthState(auth);
  const navigate = useNavigate ();
  useEffect(() => {
    if (loading) {
      return;
    }
    if (user) navigate ("/userdashboard");
  }, [user, loading]);
  return (
    <div>
      <img src={hallway} alt="hallway background" className="fixed top-[20px] w-[1550px] h-full bg-cover blur-[2px]" />
      {/* <div className="login-bg" /> */}
      <main className="login">
        <div className="login-container">
          <form action className="login-form">
            <h3>SIGN IN</h3>
            <label htmlFor="email-address">Email Address
              <input type="email" name="email-address" id="email-address" value={email} onChange={(e) => setEmail(e.target.value)} placeholder="Email Address" className="bg-[#051F6A]" />
            </label>
            <label htmlFor="password">Password
              <input type="password" name="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
            </label>
            <p><a href="/reset" className="hover:underline">Forgot Password</a></p>
            <p>Don't have an account? <Link to="/register">Register </Link>now</p>
            <button type="" className="gen-button" onClick={() => logInWithEmailAndPassword(email, password)}>SIGN IN</button>
            <button type="" className="gen-button" onClick={() => signInWithGoogle}>SIGN IN WITH GOOGLE</button>
          </form>
        </div>
      </main>
    </div>
  );
}
