import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import { auth, logInWithEmailAndPassword, signInWithGoogle } from "../firebase_setup/firebase";
import { useAuthState } from "react-firebase-hooks/auth";

export default function Login() {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, loading, error] = useAuthState(auth);
  const navigate = useNavigate();

  useEffect(() => {
    if (loading) {
      return;
    }
    if (user) navigate("/userdashboard");
  }, [user, loading]);

  return (
    <div className="login">
      <div className="login-container">
        <div className="login-form">
          <h3 className="login-title">SIGN IN</h3>
          <label>
            Email Address
            <input
            type="email"
            className="text-black"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="E-mail Address"
            />
          </label>
          <label>
            Password
            <input
            type="password"
            className="text-black"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            />
          </label>
          <p><Link to="/reset" className="underline hover:text-stone-400">Forgot Password</Link></p>
          <button
          className="login-buttons"
          onClick={() => logInWithEmailAndPassword(email, password)}>
            Login
          </button>
          <button className="login-buttons" onClick={signInWithGoogle}>
            Login with Google
          </button>
          <p><Link to="/reset">Forgot Password</Link></p>
          <p>Don't have an account? <Link to="/register">Register</Link> now.</p>
        </div>
      </div>
    </div>
  );
}
